#include "Tree/Util.hpp"#include "GirlController.hpp"GirlController::GirlController( boost::shared_ptr<Girl> g ) : girl( g ){}bool GirlController::HandleEvent( sf::Event &e ){    if( e.Type == sf::Event::KeyPressed &&        e.Key.Code == sf::Key::Space ) {        girl->DoAction();    }    return true;}void GirlController::Update( float dt ){    const sf::Input *input = &Tree::GetInput();    if( input->IsKeyDown( sf::Key::Left ) ) {        girl->MoveLeft();    }    else if( input->IsKeyDown( sf::Key::Right ) ) {        girl->MoveRight();    }    else if( input->IsKeyDown( sf::Key::Up ) ) {        girl->MoveUp();    }    else if( input->IsKeyDown( sf::Key::Down ) ) {        girl->MoveDown();    }    else {        girl->Stop();    }}